package com.library.library.controllers;

import com.library.library.entities.User;
import com.library.library.repositories.UserRepository;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.web.bind.annotation.*;

import java.util.HashMap;

@RestController // This means that this class is a Controller
@RequestMapping(path="/library") // This means URL's start with /library (after Application path)
public class MainController {

    @Autowired(required = false) // This means to get the bean called userRepository
    // Which is auto-generated by Spring, we will use it to handle the data
    private UserRepository userRepository;

    @GetMapping(path="/objects/test")
    public HashMap<String, String> hello(){
        HashMap<String, String> map = new HashMap<>();
        map.put("Pawel", "Pawel");
        map.put("Test", "Test");

        return map;
    }
}
